let Orders = () => {

  let ordersArray = [] 

  for (let i=0; i < 4; i++){
    ordersArray.push(ingredients[Math.floor(Math.random() * ingredients.length)])
  }

  let object = {1: ordersArray}
  let object2 = {2: ordersArray}

  let newObject = Object.assign({}, object, object2)

  return ordersArray

}


