import React from "react";

const Login = props => {
  return (
     <div>
                       <br/>
                <br/>
                <br/>
                <br/>
                <br/>
                <br/>
                <br/>
       { !props.currentPlayer ?
      <form onSubmit={props.onLogin}>
        <h1></h1><label>Login:</label>
        <input
          type="text"
          value={props.loginName}
          onChange={props.getLoginName}
        />
        <button>Submit</button>
      </form>
     : null}
     </div>
    )
};

export default Login;
